pe: 'html', context: document.body, global: false, async: false,            success: function (data) {                return data;            }        }).responseText;        if(typeof data !== "undefined") {            if (data == '10') {                //testirovanie zaversheno                location.href = '/guest/logout.php';            }            if (data == '11') {                //testirovanie idet                $('.mssg1_auth_reg').hide();            }            if (data == '12') {                //seichas testirovanie ne provoditsa                $('.mssg1_auth_reg').show().html('Сейчас тестирование не проводится.');            }            if (data == '13') {                //vrema na testirovanie zakonchilos, a sobesedovanie ne proideno                $('.mssg1_auth_reg').show().html('Время, отведённое для прохождения тестирования, закончилось.');            }            if (data == '20') {                //sobesedovanie zaversheno                location.href = '/guest/logout.php';            }            if (data == '21') {                //sobesedovanie idet                $('.mssg1_auth_reg').hide();            }            if (data == '22') {                //seichas sobesedovanie ne provoditsa                $('.mssg1_auth_reg').show().html('Сейчас собеседование не проводится.');            }            if (data == '23') {                //sobesedovanie i testirovanie proideni                $('.mssg1_auth_reg').hide();            }        }    }    //-------------------------------------------------------------------------------------------------------//    $(document).ready(function() {        if(typeof $('#main_body_sobesedovanie').attr('id') !== "undefined") {            table_sobesedovanie_show();        }        setInterval(function(){check_time_testirovanie_end()},10000);    });    //-------------------------------------------------------------------------------------------------------//</script><?phpsite_foot();?>